<?// using opening tag <? vs <?php because jareds godaddy server only recognizes short tags//    ini_set('display_errors', 1);//    ini_set('display_startup_errors', 1);//    error_reporting(E_ALL);//    ini_set('session.save_path',realpath(dirname($_SERVER['DOCUMENT_ROOT']) . '/sessions'));//    ini_set('session.gc_probability', 1);//    //    //https://thisinterestsme.com/fix-php-sessions-not-saving///    //https://thisinterestsme.com/check-if-session-started-php/    ////    echo "session_status = " . session_status() . PHP_EOL;//    // results://    //0 – PHP_SESSION_DISABLED: Sessions are currently disabled.//    //1 – PHP_SESSION_NONE: Sessions are enabled, but no session has been started.//    //2 – PHP_SESSION_ACTIVE: Sessions are enabled and a session has been started.//    if(session_status() == PHP_SESSION_NONE)//    {//        session_start();//    }  try {    /* PUT data comes in on the stdin stream */    $putdata = fopen("php://input", "r");           $contents = '';    while (!feof($putdata))        $contents .= fread($putdata, 8192);        /* Close the stream */        fclose($putdata);        $data = base64_decode($contents);            $dateTime = str_replace(array(" ", ":"), "_", date('Md Y G:i:s'));    $unique = $dateTime . "_" . bin2hex(random_bytes(20));        $dirParts = explode('/', __DIR__);    array_pop($dirParts);    array_push($dirParts, "Layouts", $unique);    $newDir = implode("/", $dirParts);    mkdir($newDir, 0755, TRUE);                //$uniqueDir = implode("/", array("Layouts", $unique));    //make unique directory for this user's share    //mkdir(implode("/", array(__DIR__, $uniqueDir)));    //create a web and file path for the screenshot    //    $fileName = 'FG_Layout.jpg'; //    $jpgPath = implode("/", array(__DIR__, $uniqueDir, $fileName));    $jpgPath = $newDir . "/" . "FG_Layout.jpg";    file_put_contents($jpgPath, $data);         chmod($jpgPath, 0755);        //$pathPieces = explode("/", __DIR__);         //$pathPieces[4] is 'WebGL'     $pathPieces = explode('/', $jpgPath);    //echo 'http://' . $_SERVER['HTTP_HOST'] . '/' . implode("/", array($pathPieces[4], "Layouts", $unique, $fileName));     echo 'http://' . $_SERVER['HTTP_HOST'] . '/' . implode("/", array($pathPieces[4], $pathPieces[5], $pathPieces[6])); } catch (Exception $ex) {    echo "Unable to save layout. ".$ex->getMessage();}?>